mapper.select:
(SELECT t.id ,t.parent_order ,t.ref_type ,t.ref_order ,t.deal_node ,t.deal_note ,t.status ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile ,t.start_datetime ,t.end_datetime ,t.speed_time ,t.team_code FROM tsys_biz_log t INNER JOIN tsys_role_node trn ON t.deal_node = trn.node_code left join tdq_budget_order b on b.code = t.parent_order where trn.role_code = #{roleCode} and b.is_logistics = #{isLogistics} and t.status = #{status}
AND t.ref_type = #{refType}
AND t.ref_order = #{refOrder}
AND t.team_code = #{teamCode}
order by t.start_datetime desc) union (SELECT t.id ,t.parent_order ,t.ref_type ,t.ref_order ,t.deal_node ,t.deal_note ,t.status ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile ,t.start_datetime ,t.end_datetime ,t.speed_time ,t.team_code FROM tsys_biz_log t where t.ref_type = '012' and t.status = #{status}
AND t.ref_type = #{refType}
AND t.ref_order = #{refOrder}
AND t.team_code = #{teamCode}
order by t.start_datetime desc) union (SELECT t.id ,t.parent_order ,t.ref_type ,t.ref_order ,t.deal_node ,t.deal_note ,t.status ,t.operate_role ,t.operator ,t.operator_name ,t.operator_mobile ,t.start_datetime ,t.end_datetime ,t.speed_time ,t.team_code FROM tsys_biz_log t left join tdq_credit c on t.parent_order = c.code where t.ref_type = '001' and t.status = #{status}
AND t.ref_type = #{refType}
AND t.ref_order = #{refOrder}
AND t.team_code = #{teamCode}
order by t.start_datetime desc)

mapper.update:
UPDATE tsys_biz_log
SET
parent_order = #{parentOrder}
,ref_type = #{refType}
,ref_order = #{refOrder}
,deal_node = #{dealNode}
,deal_note = #{dealNote}
,status = #{status}
,operate_role = #{operateRole}
,operator = #{operator}
,operator_name = #{operatorName}
,operator_mobile = #{operatorMobile}
,start_datetime = #{startDatetime}
,end_datetime = #{endDatetime}
,speed_time = #{speedTime}
,team_code = #{teamCode}
WHERE id = #{id}

UPDATE tsys_biz_log
SET
end_datetime = #{endDatetime}
,speed_time = #{speedTime}
WHERE id = #{id}
